<template>
    <label for="image" class="cover_label">
        <img style="height: auto; width: 150px; display: inline-flex;" :src="cover === undefined ? image : cover" alt="Card image" class="mx-auto">
        <input type="file" name="image" ref="image" hidden @change="upload" id="image">
    </label>
</template>

<script>
export default {
    name: "CasesImageUploader",
    props: {
        cover: {
            type: String,
            required: false
        }
    },
    data() {
        return {
            image: "data:image/jpeg;base64,"
        }
    },
    methods: {
        upload() {
            this.showImage()
            this.dispatchImage()
        },

        showImage() {
            let reader = new FileReader()
            reader.onload = (event) => {
                this.image = event.target.result
            }
            reader.readAsDataURL(this.$refs.image.files[0])
        },

        dispatchImage() {
            this.$emit("loaded", this.$refs.image.files[0])
        }
    }
}
</script>

<style scoped>
.cover_label {
    position: relative
}

.cover_label:hover {
    cursor: pointer
}

.cover_label:hover:after {
    background-color: rgba(0, 0, 0, 0.5)
}

</style>